    CASE 
      WHEN TR.ORDINE IN (1565) THEN NVAL(D.COL1)/1000
      WHEN TR.ORDINE IN (1566) THEN NVAL(D.COL2)/1000
      WHEN TR.ORDINE IN (1567) THEN NVAL(D.COL3)/10000
      WHEN TR.ORDINE IN (1568) THEN NVAL(D.COL4)/10000
      WHEN TR.ORDINE IN (1569) THEN NVAL(D.COL4)/NOZERO(D.COL2)
    END END) AS COL4,
  SUM(CASE WHEN D.ORDINE IN (3) AND D.PERIOADA IN (:pPERIOADA) THEN 
    CASE 
      WHEN TR.ORDINE IN (1565) THEN NVAL(D.COL1)/1000
      WHEN TR.ORDINE IN (1566) THEN NVAL(D.COL2)/1000
      WHEN TR.ORDINE IN (1567) THEN NVAL(D.COL3)/10000
      WHEN TR.ORDINE IN (1568) THEN NVAL(D.COL4)/10000
      WHEN TR.ORDINE IN (1569) THEN NVAL(D.COL4)/NOZERO(D.COL2)
    END END) AS COL5,
  SUM(CASE WHEN D.ORDINE IN (4) AND D.PERIOADA IN (:pPERIOADA) THEN 
    CASE 
      WHEN TR.ORDINE IN (1565) THEN NVAL(D.COL1)/1000
      WHEN TR.ORDINE IN (1566) THEN NVAL(D.COL2)/1000
      WHEN TR.ORDINE IN (1567) THEN NVAL(D.COL3)/10000
      WHEN TR.ORDINE IN (1568) THEN NVAL(D.COL4)/10000
      WHEN TR.ORDINE IN (1569) THEN NVAL(D.COL4)/NOZERO(D.COL2)
    END END) AS COL6,
  SUM(CASE WHEN D.ORDINE IN (5) AND D.PERIOADA IN (:pPERIOADA) THEN 
    CASE 
      WHEN TR.ORDINE IN (1565) THEN NVAL(D.COL1)/1000
      WHEN TR.ORDINE IN (1566) THEN NVAL(D.COL2)/1000
      WHEN TR.ORDINE IN (1567) THEN NVAL(D.COL3)/10000
      WHEN TR.ORDINE IN (1568) THEN NVAL(D.COL4)/10000
      WHEN TR.ORDINE IN (1569) THEN NVAL(D.COL4)/NOZERO(D.COL2)
    END END) AS COL7,
  SUM(CASE WHEN D.ORDINE IN (6) AND D.PERIOADA IN (:pPERIOADA) THEN 
    CASE 
      WHEN TR.ORDINE IN (1565) THEN NVAL(D.COL1)/1000
      WHEN TR.ORDINE IN (1566) THEN NVAL(D.COL2)/1000
      WHEN TR.ORDINE IN (1567) THEN NVAL(D.COL3)/10000
      WHEN TR.ORDINE IN (1568) THEN NVAL(D.COL4)/10000
      WHEN TR.ORDINE IN (1569) THEN NVAL(D.COL4)/NOZERO(D.COL2)
    END END) AS COL8,
  SUM(CASE WHEN D.ORDINE IN (7) AND D.PERIOADA IN (:pPERIOADA) THEN 
