      WHEN TR.ORDINE IN (1565) THEN NVAL(D.COL1)/1000
      WHEN TR.ORDINE IN (1566) THEN NVAL(D.COL2)/1000
      WHEN TR.ORDINE IN (1567) THEN NVAL(D.COL3)/10000
      WHEN TR.ORDINE IN (1568) THEN NVAL(D.COL4)/10000
      WHEN TR.ORDINE IN (1569) THEN NVAL(D.COL4)/NOZERO(D.COL2)
    END END) AS COL9,
  SUM(CASE WHEN D.ORDINE IN (8) AND D.PERIOADA IN (:pPERIOADA) THEN 
    CASE 
      WHEN TR.ORDINE IN (1565) THEN NVAL(D.COL1)/1000
      WHEN TR.ORDINE IN (1566) THEN NVAL(D.COL2)/1000
      WHEN TR.ORDINE IN (1567) THEN NVAL(D.COL3)/10000
      WHEN TR.ORDINE IN (1568) THEN NVAL(D.COL4)/10000
      WHEN TR.ORDINE IN (1569) THEN NVAL(D.COL4)/NOZERO(D.COL2)
    END END) AS COL10
FROM 
  (SELECT
      D.PERIOADA,
      D.FORM,
      D.FORM_VERS,
      TR.CFOJ,
      TR.ORDINE,
--      D.RIND,
      NVAL(SUM(CASE WHEN TR.CFOJ LIKE '%'|| D.CFOJ ||'%' THEN D.COL1 END)) AS COL1,
      NVAL(SUM(CASE WHEN TR.CFOJ LIKE '%'|| D.CFOJ ||'%' THEN D.COL2 END)) AS COL2,
      NVAL(SUM(CASE WHEN TR.CFOJ LIKE '%'|| D.CFOJ ||'%' THEN D.COL3 END)) AS COL3,
      NVAL(SUM(CASE WHEN TR.CFOJ LIKE '%'|| D.CFOJ ||'%' THEN D.COL4 END)) AS COL4,
      NVAL(SUM(CASE WHEN TR.CFOJ LIKE '%'|| D.CFOJ ||'%' THEN D.COL5 END)) AS COL5
    FROM
      CIS2.VW_DATA_ALL D
      CROSS JOIN 
      (
    SELECT '500+530+540+541+550+590+690+880+890+684+685+686+965' AS CFOJ, 1 AS ORDINE FROM DUAL UNION
    SELECT '500+530+540+541+550+590+690+880+890' AS CFOJ, 2 AS ORDINE FROM DUAL UNION
    SELECT '684+685+686' AS CFOJ, 3 AS ORDINE FROM DUAL UNION
    SELECT '686' AS CFOJ, 4 AS ORDINE FROM DUAL UNION
    SELECT '684+685' AS CFOJ, 5 AS ORDINE FROM DUAL UNION
    SELECT '685' AS CFOJ, 6 AS ORDINE FROM DUAL UNION
    SELECT '684' AS CFOJ, 7 AS ORDINE FROM DUAL UNION
    SELECT '965' AS CFOJ, 8 AS ORDINE FROM DUAL
      ) TR
    WHERE
      D.PERIOADA IN (:pPERIOADA,:pPERIOADA-4) AND 
      D.FORM_VERS = :pFORM_VERS     AND    
      (:pID_MDTABLE=:pID_MDTABLE) AND
      D.CUATM_FULL LIKE '%'||:pCOD_CUATM||';%' AND

      D.FORM IN (45)AND   
      D.CAPITOL IN (399,400,401,402)  AND
--      D.RIND IN ('2401','2402','2403','2404','2405','2406','2407','2408')
    -- D.RIND IN ('2401','2402','2403','2404','2405','2406','2407','2408','2409','2410')
    ---2019
    D.RIND IN ('2100','2110')
    GROUP BY
      D.PERIOADA,
      D.FORM,
      D.FORM_VERS,
--      D.RIND,
      TR.CFOJ,
      TR.ORDINE
  
  ) D
  CROSS JOIN 
  (
